#include<iostream>
#include<cmath>
using namespace std;
typedef long long ll;
typedef double db;
int _;
db h,c,t;
db f(ll);//计算温差
int main(){
	for(cin>>_;_;_--){
		cin>>h>>c>>t;
		ll ans;
		if(t==h){
			ans=1;
		}else if(t<=(h+c)/2.0){
			ans=2;
		}else{
			ll x=(h-t)/(2*t-h-c);
			//x或x+1轮水 再倒一杯热水
			ans=f(2*x+1)<=f(2*x+3)?2*x+1:2*x+3;
		}
		cout<<ans<<endl;
	}
	return 0;
}
db f(ll day){
	ll x=day+1>>1;//热水
	ll y=day>>1;//冷水
	db tb=(x*h+y*c)/(db)day;//水温
	return fabs(tb-t);//温差
}
```
