            if(a[i] > a[i-1]){
                while(a[i+1] > a[i] && i < n) ++i; 
                b[++b[0]] = a[i];
                continue;
            } else {
                while(a[i+1] < a[i] && i < n) ++i;
                b[++b[0]] = a[i];
                continue;                
