#include <iostream>
#include <vector>

using namespace std;

const int MAXN = 1e5 + 5;

int a[MAXN], n, t;

void Solve() {
	cin >> n;
	for(int i = 1; i <= n; i++) {
		cin >> a[i];
	}
	vector<int> v;
	v.push_back(a[1]);
	for(int i = 2; i < n; i++) {
		if((a[i - 1] <= a[i] && a[i] <= a[i + 1]) || (a[i - 1] >= a[i] && a[i] >= a[i + 1])) {
			continue;
		}
		v.push_back(a[i]);
	}
	v.push_back(a[n]);
	cout << v.size() << endl;
	for(auto i : v) {
		cout << i  << ' ';
	}
	cout << endl;
}

int main() {
	cin >> t;
	while(t--) {
		Solve();
	}
}
