typedef unsigned int uint;
typedef long long ll;
typedef unsigned long long ull;
typedef pair<int,int> pii;

const int MAXN=1e6;
int n,a[MAXN+5],b[MAXN+5];
int check(int b1_to_a1){
	int v=b[1]-b1_to_a1;
	for(int i=2;i<=n;++i){
		if(a[i]>v){
			if(b[i]<a[i]-v){
				return -1;
			}
			v=b[i]-(a[i]-v);
		}
		else v=b[i];
	}
	if(v<a[1]-b1_to_a1)
		return 1;
	else
		return 0;
}
int main() {
	int T;cin>>T;while(T--){
		cin>>n;
		for(int i=1;i<=n;++i)cin>>a[i];
		for(int i=1;i<=n;++i)cin>>b[i];
		int l=0,r=b[1];
		int ans=-1;
		while(l<=r){
			int mid=(l+r)>>1;
			int t=check(mid);
			if(t==0){
				ans=l;
				break;
			}
			if(t==-1)
				r=mid-1;
			else
				l=mid+1;
		}
		if(ans==-1)
			cout<<"NO"<<endl;
		else
			cout<<"YES"<<endl;
	}
	return 0;
}
