#include<bits/stdc++.h>
using namespace std;
typedef long long ll;
const int N=1e4+10,M=2e4+10,K=(1<<10)+10;
const int INF=0x3f3f3f3f;
int n,m,k,q,s,t,cur[N],gap[N],dep[N],qt[N];
//cur[i]:当前弧优化,gap[i]:GAP优化 
int fst[N],tot=2;
int f[K],g[K],lim,L[K],l[K],U[N],I[N];
struct mdzz{int u,v,w;}id[24];
struct EDGE{int nxt,to,val;}e[M],E[M][K];
inline int read()
{
	int s=0,w=1;
	char ch=getchar();
	while(ch<'0'||ch>'9'){if(ch=='-')w=-1;ch=getchar();}
	while(ch>='0'&&ch<='9') s=s*10+ch-'0',ch=getchar();
	return s*w;
}
inline int max(int a,int b){return a>b?a:b;}
inline int min(int a,int b){return a<b?a:b;}
inline void add(int u,int v,int w)
{
	e[tot]=(EDGE){fst[u],v,w},fst[u]=tot;++tot;
	e[tot]=(EDGE){fst[v],u,0},fst[v]=tot;++tot;
}
inline bool bfs()
{
	for(int i=1;i<=n;++i)
	cur[i]=fst[i],dep[i]=qt[i]=0;
	int hd=0,tl=1;
	dep[s]=1;qt[tl]=s;
	//这是从源点开始
	while(hd<tl)
	{
		int u=qt[++hd];
		for(int i=fst[u];i;i=e[i].nxt)
		{
			int v=e[i].to;
			if(!dep[v]&&e[i].val)
			{
				dep[v]=dep[u]+1;
				qt[++tl]=v;
				if(v==t)return dep[t];
			}
		}
	}
	return dep[t];
}
int dfs(int u,int lim)
{
	if(u==t)return lim;
	int ans=0,tmp;
	for(int i=cur[u];i;i=e[i].nxt)
	{
		int v=e[i].to;
		cur[u]=i;
		if(dep[v]==dep[u]+1&&e[i].val)
		{
			tmp=dfs(v,min(lim,e[i].val));
			e[i].val-=tmp,lim-=tmp;
			ans+=tmp,e[i^1].val+=tmp;
			if(!lim)break;
		}
	}
	return ans;
}
inline int dinic()
{
	int ans=0,tmp;
	while(bfs())ans+=dfs(s,INF);
	return ans;
}
inline int BFS()
{
	for(int i=s;i<=t;++i)dep[i]=qt[i]=0;
//	int hd=0,tl=1;
	dep[s]=1;qt[1]=s;
	for(int hd=1,tl=1;hd<=tl;++hd)
	{
		int u=qt[hd];
		for(int i=fst[u];i;i=e[i].nxt)
		{
			int v=e[i].to;
			if(!dep[v]&&e[i].val)
			{
				dep[v]=dep[u]+1;
				U[v]=u,I[v]=i;
				qt[++tl]=v;
			}
			if(dep[t])break;
		}
		if(dep[t])break;
	}
	if(!dep[t])return 0;
	int ans=25,u=t;
	while(u!=s)ans=min(ans,e[I[u]].val),u=U[u];
	u=t;
	while(u!=s)e[I[u]].val-=ans,e[I[u]^1].val+=ans,u=U[u];
	return ans;
}
inline int FF()
{
	int ans=0,tmp;
	while(tmp=BFS())ans+=tmp;
	return ans;
}
inline int lowbit(int i){return i&(-i);}
int main()
{
	n=read(),m=read(),k=read(),q=read();
	lim=(1<<k)-1,s=1,t=n;
	for(int i=1;i<=m;++i)
	{
		int u=read(),v=read(),w=read();
		add(u,v,w);
	}
	f[0]=dinic();
	for(int i=2;i<tot;++i)E[i][0]=e[i];
	L[1]=1;
	for(int i=2;i<=lim;i<<=1)L[i]=L[i>>1]+1;
	for(int i=1;i<=lim;++i)
	{
		l[i]=lowbit(i);
		for(int j=2;j<tot;++j)e[j]=E[j][i^l[i]];
		e[L[l[i]]<<1].val=25;
		f[i]=FF()+f[i^l[i]];
		for(int j=2;j<tot;++j)E[j][i]=e[j];
	}
	while(q--)
	{
		for(int i=1;i<=k;++i)id[i].w=read();
		int ans=INF;
		for(int i=1;i<=lim;++i)g[i]=g[i^l[i]]+id[L[l[i]]].w;
		for(int i=0;i<=lim;++i)ans=min(ans,f[i]+g[i^lim]);
		printf("%d\n",ans);
	}
	return 0;
}
