#include<algorithm>
int a[3];
int main(){
	int t;
	scanf("%d",&t);
	while(t--){
		scanf("%d%d%d",&a[0],&a[1],&a[2]);
		std::sort(a,a+3);
		if(a[1]!=a[2]){
			puts("NO"); continue;
		}
		puts("YES");
		printf("%d %d %d\n",1,a[0],a[1]);
	}
	return 0;
}
```

