#include<cstdio>
#include<cstring>
using namespace std;
const int MAXN=55;
bool flag[MAXN];//标记
int read(){
	int num=0,sign=1; char ch=getchar();
	while(ch<'0'||ch>'9'){if(ch=='-')sign=-1;ch=getchar();}
	while(ch>='0'&&ch<='9'){num=num*10+ch-'0';ch=getchar();}
	return num*sign;
}
int main(){
int T=read();while(T--){
	memset(flag,0,sizeof(flag));//注意清空
	int n=read(),a;
	for(int i=1;i<=2*n;i++){
		a=read();
		if(flag[a]) continue;//第二次，直接跳过
		flag[a]=1;//第一次，标记
		printf("%d ",a);//输出
	}
	printf("\n");//注意换行
}
	return 0;//华丽结束
}
```
