inline bool isYuanYin(char c)
{
	return c=='a' || c=='e' || c=='i' || c=='o' || c=='u';
}

inline string judgeType(string a[])
{
	if(a[0]==a[1] && a[1]==a[2] && a[2]==a[3])
		return "aaaa";
	if(a[0]==a[1] && a[2]==a[3])
		return "aabb";
	if(a[0]==a[3] && a[1]==a[2])
