	if(n>=4&&m>=4){
		puts("-1");
		return 0;
	}
	for(int i=1;i<=n;i++)
		for(int j=1;j<=m;j++){
			char ch=getchar();
			for(;ch!='0'&&ch!='1';ch=getchar());
			a[i][j]=(ch=='1');
		}
