		puts("0");
		return 0;
	}
	if(n==2){
		int ans1=0,ans2=0;
		for(int j=1;j<=m;j++)
			if((a[1][j]+a[2][j])%2!=(j&1))ans1++;
		for(int j=1;j<=m;j++)
			if((a[1][j]+a[2][j])%2!=((j&1)^1))ans2++;
		cout<<min(ans1,ans2);
