{
    ios::sync_with_stdio(false);
    cin>>t;
    while(t--)
    {
        cin>>n>>k;
        for(int i=1;i<=n;++i)
        {
            cin>>a[i];
        }
        if(k&1)
        {
            for(int i=1;i<=n;++i)
            {
                a[i]=-a[i];
            }
            int Min=INF;
            for(int i=1;i<=n;++i)
            {
                Min=min(Min,a[i]);
            }
            for(int i=1;i<=n;++i)
            {
                a[i]-=Min;
            }
        }
        else
        {
            int Min=INF;
            for(int i=1;i<=n;++i)
            {
                Min=min(Min,a[i]);
            }
            for(int i=1;i<=n;++i)
            {
                a[i]-=Min;
            }
        }
        for(int i=1;i<=n;++i)
        {
            cout<<a[i]<<' ';
        }
        cout<<endl;
    }
    return 0;
}
```

