		}
		for (int i = 1; i <= n; i++)
		{
			cin >> c[i];
		}
		ans[1] = a[1];
		for (int i = 2; i <= n; i++)
		{
			if (i == n)
			{
				if (a[i] != ans[i - 1] && a[i] != ans[1])
				{
					ans[i] = a[i];
				}
				else if (b[i] != ans[i - 1] && b[i] != ans[1])
				{
					ans[i] = b[i];
				}
				else if (c[i] != ans[i - 1] && c[i] != ans[1])
				{
					ans[i] = c[i];
				}
			}
			else
			{
				if (a[i] != ans[i - 1])
				{
					ans[i] = a[i];
				}
				else if (b[i] != ans[i - 1])
				{
					ans[i] = b[i];
				}
				else if (c[i] != ans[i - 1])
				{
					ans[i] = c[i];
				}
			}
		}
		for (int i = 1; i <= n; i++)
		{
			cout << ans[i] << ' ';
		}
		cout << endl;
