			if(ans[i-1]!=a[i])ans[i]=a[i];
			else if(ans[i-1]!=b[i])ans[i]=b[i];
			else if(ans[i-1]!=c[i])ans[i]=c[i];
        if(ans[1]!=a[n]&&ans[n-1]!=a[n])ans[n]=a[n];
	    else if(ans[1]!=b[n]&&ans[n-1]!=b[n])ans[n]=b[n];
		else if(ans[1]!=c[n]&&ans[n-1]!=c[n])ans[n]=c[n];
