		int t[maxn<<2],tag[maxn<<2];
		inline void pushup(int p){t[p]=min(t[lson],t[rson]);}
		inline void pushtag(int p,int k){t[p]+=k,tag[p]+=k;}
		inline void pushdown(int p){
			if(tag[p]){
				pushtag(lson,tag[p]),pushtag(rson,tag[p]);
				tag[p]=0;
			}
