#include<bits/stdc++.h>
#define pb push_back
#define count __builtin_popcount
using namespace std;
int const p=998244353;
typedef vector<int> vec;
int pw(int x,int y)
{
    int res=1;
    while(y)
    {
        if(y&1)res=1ll*res*x%p;
        x=1ll*x*x%p;
        y>>=1;
    }
    return res;
}
int a[65537],iv[65537],b[65537],sum[195],inv[65537],cnt[131075],
st[195],top,tmp[20],tmp2[20],tmp3[20],f[65537];
