	while (tmp){//如果tmp!=0 
		if (tmp%10){//如果tmp的个位不是零,不判断会出现除0的情况,会RE 
			if (n%(tmp%10)){
				return false;
			}
		}
		tmp /= 10;//将tmp的个位去掉 
	}
	return true;
}

int main(){
	scanf("%d",&t);
	long long ans;
	for (int i = 0;i<t;i++){
		scanf("%lld",&n);
		ans = n;
		while (!check(ans)){
			ans++;//如果ans不符合,实验下一个
		}
		printf("%lld\n",ans);
	}
	return 0;
}


```

