	register int x=0;
	register bool f=0;
	register char c=getchar();
	while(c<'0'||c>'9'){
		if(c=='-') f=1;
		c=getchar();
	}
	while(c>='0'&&c<='9'){
		x=(x<<3)+(x<<1)+c-48;
		c=getchar();
	}
	return f?-x:x;
}
char cr[200],str[200];int tt;
inline void print(int x,char k='\n') {
    if(!x) putchar('0');
    if(x < 0) putchar('-'),x=-x;
    while(x) cr[++tt]=x%10+'0',x/=10;
    while(tt) putchar(cr[tt--]);
    putchar(k);
}
const int maxn=523;
int lin[maxn*maxn],n,m;
int mp[maxn][maxn];
signed main(){
	int T=read();
	while(T--){
		n=read();m=read();
		for(int i=1;i<=n;i++){
			for(int j=1;j<=m;j++){
				lin[mp[i][j]=read()]=i;
			}
		}
		for(int i=1;i<=m;i++){
			for(int j=1;j<=n;j++){
				int a=read();
				if(i!=1) continue;
				int wh=lin[a];
				for(int k=1;k<=m;k++){
					print(mp[wh][k],' ');
				}
				puts("");
			}
		}
	}
	return 0;
}
