#define rep(i,a,b) for(int i=(a);i<=(b);i++)
#define _rep(i,a,b) for(int i=(a);i<(b);i++)
#include<bits/stdc++.h>
typedef long long ll;
using namespace std;
ll read()
{
	char c;
	ll x=0;
	int f=1;
	c=getchar();
	while(c<'0'||c>'9')
	{
		if(c=='-')f=-1;
		c=getchar();
	}
	while(c>='0'&&c<='9')
	{
		x=(x<<1)+(x<<3)+(c^48);
		c=getchar();
	}
	return x*f;
}
void write(ll x)
{
	if(x>9)write(x/10);
	putchar(x%10+'0');
	return;
}
ll a[5005],n,b[5005],k;
int main(){
	n=read();
	for(int i=2;i<=n;i++)
	{
		cout<<"? 1 "<<i<<endl;
		fflush(stdout);
		b[i]=read();
		if(i>2)a[i]=b[i]-b[i-1];
	}
	cout<<"? 2 "<<n<<endl;
	fflush(stdout);
	k=read();
	a[1]=b[n]-k;
	a[2]=b[2]-a[1];
	cout<<"! ";
	for(int i=1;i<=n;i++)
	{
		cout<<a[i]<<" ";
	}
	cout<<endl;
	fflush(stdout);
	return 0;
}
