	int n=read(),pos=-1;
	fo(i,1,n) a[i].val=read(),a[i].num=i;
	sort(a+1,a+1+n);
	int x=0,last=0;
	fo(i,1,n){
		if(a[i].val!=last) x++;
		last=a[i].val;
		a[i].val=x;
	}
