	int t;
	cin>>t;
	while(t--){
		int n,m;
		cin>>n>>m;
		for(int i=1;i<=n;i++){
			for(int j=1;j<=m;j++)
				cin>>a[i][j];
		}
		for(int i=1;i<=n;i++){
			for(int j=1;j<=m;j++){
				if(i%2){
					if(j%2){
						if(a[i][j]%2==0)
							cout<<a[i][j]+1<<" ";
						else
							cout<<a[i][j]<<" ";
					}
					else{
						if(a[i][j]%2==0){
							cout<<a[i][j]<<" ";
						}
						else
							cout<<a[i][j]+1<<" ";
					}
				}
				else{
					if(j%2){
						if(a[i][j]%2)
							cout<<a[i][j]+1<<" ";
						else
							cout<<a[i][j]<<" ";
					}
					else{
						if(a[i][j]%2)
							cout<<a[i][j]<<" ";
						else
							cout<<a[i][j]+1<<" ";
					}
				}
			}
			cout<<endl;
		}
	}
}

