#include<bits/stdc++.h>
#define For(i,a,b) for(register int i=(a);i<=(b);++i)
#define Rep(i,a,b) for(register int i=(a);i>=(b);--i)
using namespace std;
inline int read()
{
    char c=getchar();int x=0;bool f=0;
    for(;!isdigit(c);c=getchar())f^=!(c^45);
    for(;isdigit(c);c=getchar())x=(x<<1)+(x<<3)+(c^48);
    if(f)x=-x;return x;
}
#define fi first
#define se second
#define mkp make_pair
#define pb push_back
typedef pair<int,int>pii;

int mod;
struct modint{
    int x;
    modint(int o=0){x=o;}
    modint &operator = (int o){return x=o,*this;}
    modint &operator +=(modint o){return x=x+o.x>=mod?x+o.x-mod:x+o.x,*this;}
    modint &operator -=(modint o){return x=x-o.x<0?x-o.x+mod:x-o.x,*this;}
    modint &operator *=(modint o){return x=1ll*x*o.x%mod,*this;}
    modint &operator ^=(int b){
        modint a=*this,c=1;
        for(;b;b>>=1,a*=a)if(b&1)c*=a;
        return x=c.x,*this;
    }
    modint &operator /=(modint o){return *this *=o^=mod-2;}
    modint &operator +=(int o){return x=x+o>=mod?x+o-mod:x+o,*this;}
    modint &operator -=(int o){return x=x-o<0?x-o+mod:x-o,*this;}
    modint &operator *=(int o){return x=1ll*x*o%mod,*this;}
    modint &operator /=(int o){return *this *= ((modint(o))^=mod-2);}
    template<class I>friend modint operator +(modint a,I b){return a+=b;}
    template<class I>friend modint operator -(modint a,I b){return a-=b;}
    template<class I>friend modint operator *(modint a,I b){return a*=b;}
    template<class I>friend modint operator /(modint a,I b){return a/=b;}
    friend modint operator ^(modint a,int b){return a^=b;}
    friend bool operator ==(modint a,int b){return a.x==b;}
    friend bool operator !=(modint a,int b){return a.x!=b;}
    bool operator ! () {return !x;}
    modint operator - () {return x?mod-x:0;}
};
#define maxn 505
int n,m;
modint fac[maxn],ifac[maxn];
modint f[maxn][maxn],g[maxn][maxn];
bool vf[maxn][maxn],vg[maxn][maxn];
inline void prework(int n){
	fac[0]=1;
	For(i,1,n)fac[i]=fac[i-1]*i;
	ifac[n]=1/fac[n];
	Rep(i,n-1,0)ifac[i]=ifac[i+1]*(i+1);
}
inline modint C(int n,int m){return fac[n]*ifac[m]*ifac[n-m];}

modint F(int i,int j);
modint G(int i,int j);
inline modint sum(int x){return 1ll*x*(x+1)/2%mod;}
modint F(int i,int j)
{
	if(vf[i][j])return f[i][j];
	vf[i][j]=1,f[i][j]=0;
	if(i==j){
		For(k,1,i)
			f[i][i]+=C(i-1,k-1)*(i+1)*(G(k-1,k-1)*F(i-k,i-k)+G(i-k,i-k)*F(k-1,k-1)),
			f[i][i]+=C(i-1,k-1)*G(k-1,k-1)*G(i-k,i-k)*(sum(k-1)+sum(i-k));
		return f[i][i];
	}
	f[i][j]=F(i-1,j);
	For(k,1,j)
		f[i][j]+=C(j,k)*(F(i-k-1,j-k)*G(k,k)+F(k,k)*G(i-k-1,j-k));
	return f[i][j];
}
modint G(int i,int j)
{
	if(vg[i][j])return g[i][j];
	vg[i][j]=1,g[i][j]=0;
	if(i==j){
		if(!i)return g[i][i]=1;
		For(k,1,i)
			g[i][i]+=G(k-1,k-1)*G(i-k,i-k)*C(i-1,k-1); 
		g[i][i]*=(i+1);
		return g[i][i];
	}
	g[i][j]=G(i-1,j);
	For(k,1,j)
		g[i][j]+=G(i-k-1,j-k)*G(k,k)*C(j,k);
	return g[i][j];
}

signed main()
{
	n=read(),m=read(),mod=read(),prework(501);
	cout<<F(n,m).x<<endl;
	return 0;
}
```

由于 $\text{Rainbow}$ 神仙太神了，导致我没有看懂他的题解。于是我还是做一个为官方题解添油加醋的搬运工吧。

---

首先假设 $n=m$。

定义 $dp1_i$ 为 $i$ 个位置 $i$ 个人坐满的方案数。由熟知结论，$dp1_i=(i+1)^{i-1}2^i$。

但题解中给出了这样一个递推式 $dp1_i=(i+1)\sum\limits_{j=1}^i dp1_{j-1}dp1_{i-j}\binom{i-1}{j-1}$

我们考虑它的意义，即枚举第 $i$ 个人的位置进行转移。

定义 $dp2_i$ 为 $i$ 个位置 $i$ 个人时原问题的答案。这时我们同理枚举第 $i$ 个人的位置 $j$，所以有递推式 
$$dp2_i=\sum\limits_{j=1}^i ((\binom{j}{2}+\binom{i-j+1}{2})dp1_{j-1}dp1_{i-j}+$$

$$dp2_{j-1}(i+1)dp1_{j-i}+dp2_{j-i}(i+1)dp1_{j-1})\binom{i-1}{j-1}$$

于是我们就解决了 $n=m$ 的情况，现在考虑 $n>m$ 时：

定义 $dp3_{i,j}$ 为 $i$ 个座位 $j$ 个人时无人缺座的方案数。其实这也可以直接一个式子，但是经过上面的经验，如果我们直接式子解决了就难以考虑接下来的转移。

$dp3$ 的转移颇具技巧性。我们枚举当前最长的选好的后缀 $l$，于是可以列出转移 $dp3_{i,j}=\sum\limits_{l=0}^j dp1_l\binom{j}{l} dp3_{i-l-1,j-l}$，不难发现它是不重不漏的。

类似地定义 $dp4_{i,j}$ 为 $i$ 个座位 $j$ 个人的答案，也就是我们欲求的答案。我们仍旧枚举其最长后缀 $l$，则答案为 $dp4_{i,j}=\sum\limits_{l=0}^j \binom{j}{l}(dp1_ldp4_{i-l-1}{i-l}+dp2_ldp3_{i-l-1,j-l})$

于是我们就顺水推舟地做完了，时间复杂度 $O(n^3)$

```
#include<bits/stdc++.h>
using namespace std;
#define inf 1e9
const int maxn=2e5+10;
inline int read(){
	int x=0,f=1;char c=getchar();
	while(c<'0'||c>'9'){if(c=='-')f=-1;c=getchar();}
	while(c>='0'&&c<='9'){x=(x<<1)+(x<<3)+c-'0';c=getchar();}
	return x*f;
}
const int N=5e2+5; 
int n,m,mod,C[N][N];
int dp1[N],dp2[N],dp3[N][N],dp4[N][N];
int main(){
	n=read(),m=read(),mod=read();
	dp1[0]=1;C[0][0]=1;
	for(int i=1;i<=n;i++){
		C[i][0]=1;
		for(int j=1;j<=i;j++)
			C[i][j]=(C[i-1][j]+C[i-1][j-1])%mod;
	}
	for(int i=1;i<=n;i++){
		for(int j=1;j<=i;j++){
			dp1[i]=(dp1[i]+1ll*dp1[j-1]*dp1[i-j]%mod*C[i-1][j-1])%mod;
			dp2[i]=(dp2[i]+1ll*dp2[j-1]*dp1[i-j]%mod*(i+1)%mod*C[i-1][j-1])%mod;
			dp2[i]=(dp2[i]+1ll*dp1[j-1]*dp2[i-j]%mod*(i+1)%mod*C[i-1][j-1])%mod;
			dp2[i]=(dp2[i]+1ll*dp1[j-1]*dp1[i-j]%mod*(C[j][2]+C[i-j+1][2])%mod*C[i-1][j-1])%mod;
		}dp1[i]=1ll*(i+1)*dp1[i]%mod;
	}
	dp3[0][0]=1;
	for(int i=1;i<=n;i++){
		dp3[i][0]=1;
		for(int j=1;j<i;j++)
			for(int l=0;l<=j;l++){
				dp3[i][j]=(dp3[i][j]+1ll*dp1[l]*C[j][l]%mod*dp3[i-l-1][j-l])%mod;
				dp4[i][j]=(dp4[i][j]+1ll*dp1[l]*C[j][l]%mod*dp4[i-l-1][j-l])%mod;
				dp4[i][j]=(dp4[i][j]+1ll*dp2[l]*C[j][l]%mod*dp3[i-l-1][j-l])%mod;
			}
		dp3[i][i]=dp1[i];dp4[i][i]=dp2[i];
	}
	printf("%d\n",dp4[n][m]);
    return 0;
}
```
