		for(int i=1;i<=n;i++)for(int j=1;j<=m;j++){
			if(a[i][j]<0)cnt++;ans+=abs(a[i][j]);Min=min(Min,abs(a[i][j]));
		}
		if(cnt%2)printf("%d\n",ans-Min*2);
		else printf("%d\n",ans);
	}
}


```
