	bool flag;
	while (T--) {
		scanf("%d%d", &n, &k);
		for (int i=1; i<=n; i++)
			scanf("%d%d", &x[i], &y[i]);
		for (int i=1; i<=n; i++) {
			flag = 1;
			for (int j=1; j<=n; j++) {
				if (Abs(x[i] - x[j]) + Abs(y[i] - y[j]) > k) {
					flag = 0;
					break;
				}
