using namespace std;
int main()
{
	int T;
	scanf("%d",&T);
	while(T--)
	{
		int n;
		scanf("%d",&n);
		if(n==1)
		{
			printf("0\n");
		}
		else
		if(n==2)
		{
			printf("1\n");
		}
		else
		if(n==3)
		{
			printf("2\n");
		}
		else
		if(n%2==1)
		{
			printf("3\n");
		}
		else
