const int N=200005;
int T;
int n,m;
int b1[N],b2[N];
int a[N];
int main()
{
	scanf("%d",&T);
	while(T--)
	{
		memset(a,0,sizeof a);
		memset(b1,0,sizeof b1);
		memset(b2,0,sizeof b2);
		scanf("%d%d",&n,&m);
		long long sum=0;
		for(int i=1;i<=n;i++)
		{
			scanf("%d",&a[i]);sum+=a[i];
		}
		for(int i=1;i<=n;i++)
		{
			int x;scanf("%d",&x);
			if(x==1)b1[++b1[0]]=a[i];
			else 	b2[++b2[0]]=a[i];
		}
		if(sum<m)
		{
			puts("-1");continue;
		}
		sort(b1+1,b1+1+b1[0]);reverse(b1+1,b1+1+b1[0]);
		sort(b2+1,b2+1+b2[0]);reverse(b2+1,b2+1+b2[0]);
		int ans=0;
		int h1=1,h2=1;
		while(m>0)
		{
			if(m-b1[h1]<=0)
			{
				ans++;
				m-=b1[h1++];
			}
			else if(b2[h2]>=b1[h1]+b1[h1+1])
			{
				ans+=2;m-=b2[h2++];
			}
			else
			{
				ans+=1;m-=b1[h1];
				h1+=1;
			}
		}
		printf("%d\n",ans);
	}
	return 0;
} 
