	bool operator < (query b) const
	{
		if(l/unit!=b.l/unit)return l<b.l;
		if(r/unit!=b.r/unit)
		{
			if((l/unit)&1)return r<b.r;
			return r>b.r;
		}
		if((r/unit)&1)return t<b.t;
		return t>b.t;
	}
