		while(r>q[i].r)del(a[r--]);
		while(t<q[i].t)upd(i,++t);
		while(t>q[i].t)upd(i,t--);
		ans[q[i].id]=query(q[i].k);
	} 
	for(int i=1;i<=cntq;i++)printf("%d\n",ans[i]);
	return 0;
