                printf("\n");
            }
            else
            {
                if (m % 2 == 0)
                    printf("NO\n");
                else
                {
                    printf("YES\n");
                    for (int i = 0; i <= m; ++i)
                        printf("%d ", (i & 1) + 1);
                    printf("\n");
                }
            }
        }
        else
        {
            if (a[1][2] == a[2][3] && a[1][2] == a[3][1])
            {
                printf("YES\n");
                for (int i = 0; i <= m; ++i)
                    printf("%d ", (i % 3) + 1);
                printf("\n");
            }
            else
            {
                int x, y, z, cnt = (m - 1) / 2;
                if (a[1][2] == a[2][3])
                    x = 3, y = 1, z = 2;
                else if (a[1][2] == a[3][1])
                    x = 2, y = 3, z = 1;
                else
                    x = 1, y = 2, z = 3;
                deque<int> ans;
                if (m & 1)
                {
                    ans.push_back(x);
                    ans.push_back(y);
                }
                else
