                    ans.push_back(y);
                    ans.push_back(z);
                    ans.push_back(x);
                }
                for (int i = 1; i <= cnt; ++i)
                {
                    int now = ans.back();
                    if (now == 3)
                        ans.push_back(1);
                    else
                        ans.push_back(now + 1);
                }
                for (int i = 1; i <= cnt; ++i)
                {
                    int now = ans.front();
                    if (now == 1)
                        ans.push_front(3);
                    else
                        ans.push_front(now - 1);
                }
                printf("YES\n");
                for (auto i : ans)
                    printf("%d ", i);
                printf("\n");
            }
        }
    }
    return 0;
