	d[s]=0;q.push(make_pair(0,s));
	while(!q.empty()){
		int x=q.top().second;q.pop();
		v[x]=1;
		for(int i=h[x];i;i=e[i].nxt)if(d[x]+e[i].val<d[e[i].to]){
			d[e[i].to]=d[x]+e[i].val;q.push(make_pair(-d[e[i].to],e[i].to));
