	scanf("%d%d",&n,&m);s=1;
	rep(op,1,m){
		int x,y,z;scanf("%d%d%d",&x,&y,&z);
		add(x,f(z,y),z*z);
		add(y,f(z,x),z*z);
		rep(i,1,50)add(f(i,x),y,z*z+z*i*2);
		rep(i,1,50)add(f(i,y),x,z*z+z*i*2);
