#include<bits/stdc++.h>
using namespace std;
int n, m, k, a[500010], b[500010], tong[500010], ti = 0, maxn = -1;
long long ans = 0;//以上为定义，初始化
int mod;
vector<int>l;
int solve(int day)//统计答案
{
	//solve it;
}
void exgcd(int a,int b,int &x,int &y)//gcd函数
{
    if(b == 0){x = 1;y = 0;return ;}
    exgcd(b, a % b, x, y);
    int z = x;x = y, y = z - y * (a / b);
}
int ex(int a, int b)//求公共解
{
	int d = a - b;
	if(d % __gcd(n, m))return -1;
	d = __gcd(n, m);
	int p1 = n / d, p2 = m / d;
	int r1, r2;
	exgcd(p1, p2, r1, r2);
	int x = a + (b - a) * r1 * n / d;
	return x;
}
int main()
{
	cin>>n>>m>>k;
	mod = n * m;
	for(int i = 1;i <= n;i ++)
	{
		cin>>a[i];
		tong[a[i]] = i;//读入并可以O(1)判断
	}
	for(int i = 1;i <= m;i ++)cin>>b[i];
	for(int i = 1;i <= m;i ++)
	{
		if(! tong[b[i]])continue;
		int cnt = ex(tong[b[i]] - 1, i - 1) + 1;//得出答案
		if(cnt == 0)continue;
		cnt %= mod;
		if(cnt == 0)cnt += mod;//注意如果能整除要加回去mod
		ti ++;
		l.push_back(cnt);//加进去
	}
	sort(l.begin(), l.end());
	int rev = mod - ti;
	while(k >= mod)//取模统计答案
	{
		ans += k - k % mod;
		k -= k / mod * rev;
	}
	cout<<ans + solve(k)<<endl;//输出
	return 0;
}
