```cpp
#include<iostream>
#include<cstdio>
#include<cstring>
#include<cmath>
#include<algorithm>
using namespace std;
const int Len = 1e2 + 5;
int n,m,a[Len];
int main()
{
	int t;scanf("%d",&t);
	while(t --)
	{
		scanf("%d %d",&n,&m);int sum = 0;
		for(int i = 1 ; i <= n ; i ++) 
		{
			scanf("%d",&a[i]);
			sum += a[i];
		}
		if((n == 1 && a[1] == m) || sum == m) 
		{
			puts("NO");
			continue;
		}
		while(1)
		{
			bool flag = true;
			random_shuffle(a + 1 , a + 1 + n);int xx = 0;
			for(int i = 1 ; i <= n ; i ++)
			{
				xx += a[i];
				if(xx == m) 
				{
					flag = false;
					break;
				}
			}
			if(flag) break;
		}	
		puts("YES");
		for(int i = 1 ; i <= n ; i ++) printf("%d ",a[i]); 
		puts("");
	}
	return 0;
}
```

