#include<bits/stdc++.h>
using namespace std;
int n,m,k,a[505][505],b[505][505],f[505][505][12];
int main(){
	scanf("%d%d%d",&n,&m,&k);
   if (k&1){
		for (int i=1;i<=n;i++){
