			puts("");
		}
		return 0;
	} for (int i=1;i<=n;i++)
		for (int j=1;j<m;j++)
			scanf("%d",&a[i][j]);
	for (int i=1;i<n;i++)
		for (int j=1;j<=m;j++)
			scanf("%d",&b[i][j]);
	for (int p=1;p<=k>>1;p++)
		for (int i=1;i<=n;i++)
			for (int j=1;j<=m;j++){
				f[i][j][p]=INT_MAX;
				if(j>1) f[i][j][p]=min(f[i][j][p],f[i][j-1][p-1]+a[i][j-1]);
                if(j<m) f[i][j][p]=min(f[i][j][p],f[i][j+1][p-1]+a[i][j]);
                if(i>1) f[i][j][p]=min(f[i][j][p],f[i-1][j][p-1]+b[i-1][j]);
                if(i<n) f[i][j][p]=min(f[i][j][p],f[i+1][j][p-1]+b[i][j]);
			}
	for (int i=1;i<=n;i++){
