 *  Mail : minyuenu@gmail.com
 * =============================== */
#include <bits/stdc++.h>
using namespace std;
template <typename T> inline void read(T &a){
	T w=1; a=0;
	char ch=getchar();
	for(;ch < '0' || ch > '9';ch=getchar()) if(ch == '-') w=-1;
	for(;ch>='0'&&ch<='9';ch=getchar()) a=(a<<3)+(a<<1)+(ch^48);
	a*=w;
}
template <typename T> inline void ckmax(T &a, T b){a = a > b ? a : b;}
template <typename T> inline void ckmin(T &a, T b){a = a < b ? a : b;}
#define fi first
#define se second
#define pb push_back
#define mp make_pair
#define pii pair<int, int> 
#define vi vector<int> 
#define Debug(x) cout << #x << " = " << x << endl
#define For(i,l,r) for (int i = l; i <= r; ++i) 
#define foR(i,l,r) for (int i = l; i >= r; --i)
const int N = 1e6 + 10;
const int Mod = 998244353;
inline int Addc++ (int x, int y) {
	return (x + y) >= Mod ? (x + y - Mod) : (x + y);
}
inline int Mul (int x, int y) {
	return (1ll * x * y) >= Mod ? (1ll * x * y % Mod) : (x * y); 
}
inline int Sub (int x, int y) {
	return (x - y) < 0 ? (x + Mod - y) : (x - y);
}
int qpow (int a, int c++b) {
	int ans = 1, base = a;
	while (b) {
		if (b & 1) ans = Mul(base, ans);
		base = Mul(base, base);
		b >>= 1;
	}
	return ans;
}
int dp[N], sum[N], dp2[N], inv2, inv6;
int main() {
	int n; read(n);
	inv2 = qpow(2, Mod - 2), inv6 = qpow(6, Mod - 2); 
	if (n == 1) return putchar('5'), 0;
	dp[0] = sum[0] = 1;
	dp[1] = 2, sum[1] = 3;
	for (int i = 2; i <= n; ++i) {
		dp[i] = Add(dp[i - 1], Add(Mul(dp[i - 1], sum[i - 2]), Mul(Mul(dp[i - 1], Add(dp[i - 1], 1)), inv2)));
		sum[i] = Add(sum[i - 1], dp[i]); 
	}
	int Ans = dp[n];
	Ans = Add(Ans, Mul(dp[n - 1], Mul(inv2, Mul(sum[n - 2], Add(sum[n - 2], 1)))));
	Ans = Add(Ans, Mul(sum[n - 2], Mul(inv2, Mul(dp[n - 1], Add(dp[n - 1], 1)))));
	Ans = Add(Ans, Mul(inv6, Mul(dp[n - 1], Mul(Add(dp[n - 1], 1), Add(dp[n - 1], 2)))));
	Ans = Mul(2, Ans);
	Ans -= 1;
	for (int i = 1; i <= n; ++i) dp2[i] = Sub(dp[i], dp[i - 1]); 
	for (int i = 0; i < n; ++i) 
		Ans = Add(Ans, Mul(dp[i] - 1, dp2[n - i - 1]));
	printf ("%d", Ans); 
}



```

- Update on 2022.10.18：修改表述。

> [CF1528E Mashtali and Hagh Trees](https://www.luogu.com.cn/problem/CF1528E)

考虑限制的等价描述。前两个条件很直白，我们尝试翻译第三个条件。一个直观的想法是好树必须是内向树（根向树）或外向树（叶向树），也就是整棵树必须找到一个根，使得任何一个点都与该节点具有祖先后代关系。

一棵内向树在改变其所有边方向后可以得到一棵外向树，对于外向树同理。因此，内向树和外向树一一对应。我们只需对外向树计数。

- 一条链既可以是内向树，也可以是外向树。因此最后的答案乘 $2$ 后要减 $1$。

设 $f_i$ 表示当 $n = i$ 时的答案，考虑递推。我们枚举根的度数，根据限制二，它只有可能向外伸出 $1, 2$ 或 $3$ 条边。但接最长链为 $i - 1$ 的好树时，根的度数增加了 $1$，所以对于 $i < n$，根只能伸出至多两条边。

- 对于伸出一条边，方案数即 $f_{i - 1}$。

- 对于伸出两条边，方案数即 $\dfrac {f_{i - 1}(f_{i - 1} + 1)} 2$，原因同上。注意子树之间无序，所以是 $\dbinom {f_{i - 1} + 1} 2$。

    等等，我们只要保证 **至少** 有一条链长为 $n$，所以接上来的两棵子树并不需要都有长为 $n - 1$ 的链。对于形如 **至少** 的限制一般考虑 **容斥**，用 **所有** 的方案数减去 **全都不** 的方案数。因此，设 $s$ 为 $f$ 的前缀和，则方案数为 $\dbinom{s_{i - 1} + 1} 2 - \dbinom{s_{i - 2} + 1} 2$。

根据上述分析，转移方程为
$$
f_i = f_{i - 1} + \dbinom{s_{i - 1} + 1} 2 - \dbinom{s_{i - 2} + 1} 2
$$
答案即 $f_n$ 再加上伸出去三条边的情况 $\dbinom{s_{n - 1} + 2} 3 - \dbinom{s_{n - 2} + 2} 3$。

这么写过不了样例三。

既然外向树计数没有问题，自然是限制三的转化出了点差错。仔细想想，似乎内向树和外向树并不是 “存在节点使得任何一个节点都与该节点具有祖先后代关系” 的等价条件。还有一种可能，那就是内向树和外向树拼起来，它们有同一个根。或者说，一棵内向树和一棵外向树通过一条有向链拼接在一起。

直接枚举有向链长度 $L$。为了防止重复计数，树根的度数为 $2$，即树根不能向下延伸链，否则会和有向链拼成一个更长的链。此外，$L$ 必须是正数，否则拼接处的节点度数等于 $4$。

设 $g_i$ 表示强制树根度数为 $2$ 的 $i$ 好树内向树个数，根据上述分析，
$$
g_i = \dbinom{s_{i - 1} + 1} 2 - \dbinom{s_{i - 2} + 1} 2
$$
则答案加上 $\sum\limits_{L = 1} ^ {n - 2}\sum\limits_{i = 1} ^ {n - L - 1} g_i g_{n - L - i}$，容易前缀和优化做到 $\mathcal{O}(n)$。

```cpp
#include <bits/stdc++.h>
using namespace std;
#define fi first
#define se second
#define TIME 1e3 * clock() / CLOCKS_PER_SEC
using ll = long long;
using uint = unsigned int;
using ld = long double;
// using lll = __int128;
using pii = pair<int, int>;
using pll = pair<ll, ll>;
using ull = unsigned long long;
inline ll read() {
  ll x = 0, sgn = 0;
  char s = getchar();
  while(!isdigit(s)) sgn |= s == '-', s = getchar();
  while(isdigit(s)) x = x * 10 + s - '0', s = getchar();
  return sgn ? -x : x;
}
inline void print(ll x) {
  if(x < 0) return putchar('-'), print(-x);
  if(x >= 10) print(x / 10);
  putchar(x % 10 + '0');
}
bool Mbe;
constexpr int N = 1e6 + 5;
constexpr int mod = 998244353;
constexpr int iv6 = (mod + 1) / 6;
void add(int &x, int y) {x += y, x >= mod && (x -= mod);}
int bin2(int x) {return 1ll * x * (x + 1) / 2 % mod;}
int bin3(int x) {return 1ll * x * (x + 1) % mod * (x + 2) % mod * iv6 % mod;}
int n, f[N], sf[N], g[N], sg[N];
bool Med;
int main() {
  fprintf(stderr, "%.3lf MB\n", (&Mbe - &Med) / 1048576.0);
  #ifdef ALEX_WEI
    FILE* IN = freopen("1.in", "r", stdin);
    FILE* OUT = freopen("1.out", "w", stdout);
  #endif
  cin >> n;
  f[0] = sf[0] = 1;
  for(int i = 1; i <= n; i++) {
    g[i] = bin2(sf[i - 1]);
    if(i > 1) add(g[i], mod - bin2(sf[i - 2]));
    f[i] = f[i - 1], sf[i] = sf[i - 1], sg[i] = sg[i - 1];
    add(f[i], g[i]), add(sf[i], f[i]), add(sg[i], g[i]);
  }
  int ans = f[n];
  add(ans, bin3(sf[n - 1]));
  if(n > 1) add(ans, mod - bin3(sf[n - 2]));
  add(ans, ans - 1);
  for(int i = 1; i + 1 < n; i++) add(ans, 1ll * g[i] * sg[n - i - 1] % mod);
  cout << ans << "\n";
  cerr << TIME << " ms\n";
  return 0;
}
