   int s = 0,w = 1;
   char ch = getchar();
   while(ch < '0' || ch > '9'){if(ch == '-')w = -1;ch = getchar();}
   while(ch >= '0' && ch <= '9')s = s * 10 + ch - '0',ch = getchar();
   return s * w;
}

int n,m; 
string s;
string ans = "~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~";

signed main(){
	cin>>n>>m;
	cin>>s;
	for(int i = 0;i <= n - 1;i ++){
		string ss = s.substr(0,i + 1);
		string s1 = ss;
		while(s1.length() < m)s1 = s1 + s1;
		ans = min(ans,s1.substr(0,m));
	}
	cout<<ans;
}
