#include<cstdio>
#include<cstring>
#include<cmath>
using namespace std;
typedef long long ll;
#define Maxn 200010 
int t,n,a[Maxn],f[25][Maxn];
int gcd(int a,int b){
	if(b==0) return a;
	return gcd(b,a%b);
}
int Query(int L,int R){
	int p=log2(R-L+1);
	return gcd(f[p][L],f[p][R-(1<<p)+1]);
}
bool AreYouOK(){
	for(int i=2;i<=n;i++) if(a[i]!=a[i-1]) return false;
	return true;
}
void Solve(){
	scanf("%d",&n);
	for(int i=1;i<=n;i++) scanf("%d",&a[i]),f[0][i]=a[i];
	for(int i=1;i<=log2(n);i++)
		for(int j=1;j<=(n-(1<<i)+1);j++)
			f[i][j]=gcd(f[i-1][j],f[i-1][j+(1<<(i-1))]);
	if(AreYouOK()) {
		puts("0"); return;
	}
	int L=1,R=n,ans;
	while(L<=R){
		int mid=L+R>>1;
		for(int i=1;i<=n;i++){
			if(i<=n-mid) a[i]=Query(i,i+mid);
			else a[i]=gcd(Query(i,n),Query(1,i+mid-n));
		}
		if(AreYouOK()) ans=mid,R=mid-1;
		else L=mid+1;
	}
	printf("%d\n",ans);
}
