#define putll(x) printf("%lld\n",x)
#define putull(x) printf("%llu\n",x)
#define lowbit(x) ((x)&(-(x)))
#define inf (1e9)
#define INF (1e18)
#define eps (1e-8)
I int read()
{
	char ch=getchar();
	int res=0,flag=1;
	while(ch<'0'||ch>'9')
	{
		if(ch=='-')
			flag=-1;
		ch=getchar();
	}
	while(ch>='0'&&ch<='9')
	{
		res=res*10+ch-'0';
		ch=getchar();
	}
	return res*flag;
}
int T,n; 
int main()
{
	T=read();
	while(T--)
	{
		n=read();
		int sum=0,ans=0;
		for(R int i=1;;i+=2)
		{
			sum+=i;
			++ans;
			if(sum>=n)
				break;
		}
		putint(ans);
	}
	return 0;
}
```

