#include <cstdio>
/*
   显然，beautiful array排序后相邻项差<=2
   那么显然能让差=2时令其=2最好
   贪心直到太大，减掉即可
*/
#define int long long
void once()
{
	int t;
	scanf("%lld",&t);
	int ans=0;
	int now=1;
	while(now<t)
	{
		t-=now;
		ans++;
		now+=2;
	}
	printf("%lld\n",ans+1);
}
#undef int


int main()
{
    int t;
    scanf("%d",&t);
    while(t--) once();
    return 0;
}
