> 题目链接：[CF1556H DIY Tree](https://www.luogu.com.cn/problem/CF1556H)

> **本题解同步发布于 [My Blog](http://www.cxy07.blog/index.php/archives/131/)**

题意：

> 给定 $n$ 个点，$\binom{n}{2}$ 条带权边，前 $k$ 个点是特殊点，每个特殊点 $i$ 有一个度数限制 $d_i$，求一棵边权和最小的生成树，使得该生成树中每个特殊点 $i$ 的度数 $\le d_i$。

> $2\le n\le 50, 1\le k\le \min(n-1, 5)$。

拟阵交。

对于该无向图 $G=(V,E)$，令 $M_1=(E,\mathcal{I}_1),\ \mathcal{I}_1=\{F\subseteq E:F\text{ \small 无环}\}$。则 $M_1$ 是经典的图拟阵，$\text{kruskal}$ 算法就是基于图拟阵的。

如果 “加入一些边使得每个特殊点度数满足限制” 也是拟阵，就可以令 $M_2=(E,\mathcal{I}_2),\ \mathcal{I}_2=\{F\subseteq E : F\text{ \small 使得每个特殊点 } i \text{ 的度数} \le d_i \}$。

然后求

$$
\max_{I\in \mathcal{I}_1\cap \mathcal{I}_2} \omega(I)
$$

其中 $\omega(I)=\sum_{e\in I} \omega(e)$。这样，问题就解决了。

但是实际上 “加入一些边使得每个特殊点度数满足限制” 不是拟阵。他满足遗传性，但是不满足交换性。也就是对于两组 $I,J\in \mathcal{I}_2$，且 $|J|>|I|$，不一定存在 $z\in J \backslash I$，使得 $I\cup z\in\mathcal{I}_2$。

这是因为有一些边只连接了一个关键点，他们对所有特殊点的度数贡献总和是 $1$，但连接了两个关键点的边对其贡献为 $2$。

因此，考虑将所有连接了两个特殊点的边拿出来，特殊处理。由于 $k\le 5$，所以这种边至多只有 $10$ 条，我们可以 $2^{10}$ 枚举他们是否出现，并检查是否合法。

这样，剩下的边就满足交换性了，接下来就对每一种特殊点之间的边的枚举方案，都做一遍拟阵交即可。

```cpp
//Code By CXY07
#include<bits/stdc++.h>
using namespace std;

//#define FILE
//#define int long long
#define file(FILENAME) freopen(FILENAME".in", "r", stdin), freopen(FILENAME".out", "w", stdout)
#define randint(l, r) (rand() % ((r) - (l) + 1) + (l))
#define LINE() cout << "LINE = " << __LINE__ << endl
#define debug(x) cout << #x << " = " << x << endl
#define abs(x) ((x) < 0 ? (-(x)) : (x))
#define inv(x) qpow((x), mod - 2)
#define lowbit(x) ((x) & (-(x)))
#define ull unsigned long long
#define pii pair<int, int>
#define LL long long
#define mp make_pair
#define pb push_back
#define scd second
#define vec vector
#define fst first
#define endl '\n'

const int MAXN = 3010;
const int INF = 2e9;
const double eps = 1e-6;
const double PI = acos(-1);
//const int mod = 1e9 + 7;
//const int mod = 998244353;
//const int G = 3;
//const int base = 131;

int n, k, m1, m, Ans = INF;
int d_lim[MAXN], X[MAXN], Y[MAXN], W[MAXN];
bool vis[MAXN];
vec<int> G[MAXN];

template<typename T> inline bool read(T &a) {
	a = 0; char c = getchar(); int f = 1;
	while(c < '0' || c > '9') { if(c == '-') f = -1; c = getchar(); }
	while(c >= '0' && c <= '9') { a = a * 10 + (c ^ 48); c = getchar(); }
	return a *= f, true;
}

struct M1 {
	int S;
	struct DSU {
		int f[MAXN];
		int find(int x) { return f[x] == x ? x : f[x] = find(f[x]); }
		void unity(int x, int y) { (find(x) != find(y)) && (f[find(x)] = find(y)); }
		bool Init(int S) { 
			for(int i = 1; i <= n; ++i) f[i] = i; 
			for(int i = 1; i <= m1; ++i) {
				if(!((S >> (i - 1)) & 1)) continue;
				if(find(X[i]) != find(Y[i])) unity(X[i], Y[i]);
				else return false;
			}
			return true;
		}
	} ex[MAXN], tmp;
	void Init(int _S) { S = _S; }
	bool chk(int S) { return tmp.Init(S); }
	void build() {
		for(int i = m1 + 1; i <= m + 1; ++i) {
			if(!vis[i] && i <= m) continue;
			ex[i].Init(S);
			for(int j = m1 + 1; j <= m; ++j) {
				if(!vis[j] || i == j) continue;
				ex[i].unity(X[j], Y[j]);
			}
		}
	}
	bool valid(int x) { return ex[m + 1].find(X[x]) != ex[m + 1].find(Y[x]); }
	bool valid(int x, int y) { return (ex[x].find(X[y]) != ex[x].find(Y[y])); }
} M1;

struct M2 {
	int S, d[MAXN];
	void add(int S) {
		for(int i = 1; i <= n; ++i) d[i] = 0;
		for(int i = 1; i <= m1; ++i) if((S >> (i - 1)) & 1) d[X[i]]++, d[Y[i]]++;
	}
	bool chk(int S) {
		add(S);
		for(int i = 1; i <= k; ++i) if(d[i] > d_lim[i]) return false;
		return true;
	}
	void Init(int _S) { S = _S; }
	void build() {
		add(S);
		for(int i = m1 + 1; i <= m; ++i)
			if(vis[i]) d[X[i]]++, d[Y[i]]++;
	}
	bool valid(int x) {
		d[X[x]]++, d[Y[x]]++;
		bool res = (d[X[x]] <= d_lim[X[x]] && d[Y[x]] <= d_lim[Y[x]]);
		d[X[x]]--, d[Y[x]]--; return res;
	}
	bool valid(int x, int y) {
		d[X[x]]--, d[Y[x]]--, d[X[y]]++, d[Y[y]]++;
		bool res = (d[X[y]] <= d_lim[X[y]] && d[Y[y]] <= d_lim[Y[y]]);
		d[X[x]]++, d[Y[x]]++, d[X[y]]--, d[Y[y]]--; return res;
	}
} M2;

bool SPFA(int _s, int _t) {
	queue<int> Q;
	static int dis[MAXN], len[MAXN], pre[MAXN];
	static bool inq[MAXN];
	while(Q.size()) Q.pop();
	for(int i = m1 + 1; i <= m + 2; ++i)
		dis[i] = INF, len[i] = pre[i] = 0, inq[i] = false;
	dis[_s] = 0, inq[_s] = true; Q.push(_s);
	while(Q.size()) {
		int x = Q.front(); Q.pop(); inq[x] = false;
		for(auto to : G[x]) {
			int w = vis[to] ? -W[to] : W[to];
			if(dis[to] > dis[x] + w || (dis[to] == dis[x] + w && len[to] > len[x] + 1)) {
				dis[to] = dis[x] + w, len[to] = len[x] + 1, pre[to] = x;
				if(!inq[to]) Q.push(to), inq[to] = true;
			}
		}
	}
	if(dis[_t] == INF) return false;
	for(int x = pre[_t]; x != _s; x = pre[x]) vis[x] ^= 1;
	return true;
}

bool Augment() {
	M1.build(); M2.build(); int _s = m + 1, _t = m + 2;
	for(int i = m1 + 1; i <= m + 2; ++i) G[i].clear();
	for(int i = m1 + 1; i <= m; ++i) {
		if(vis[i]) continue;
		if(M1.valid(i)) G[_s].pb(i);
		if(M2.valid(i)) G[i].pb(_t);
	}
	for(int i = m1 + 1; i <= m; ++i) {
		if(!vis[i]) continue;
		for(int j = m1 + 1; j <= m; ++j) {
			if(vis[j]) continue;
			if(M1.valid(i, j)) G[i].pb(j);
			if(M2.valid(i, j)) G[j].pb(i);
		}
	}
	return SPFA(_s, _t);
}

int Matroid_Intersection(int S) {
	int res = 0, cnt = n - 1 - __builtin_popcount(S);
	memset(vis, false, sizeof vis);
	while(Augment()) cnt--;
	if(cnt) return INF;
	for(int i = 0; i < m1; ++i) if((S >> i) & 1) res += W[i + 1];
	for(int i = m1 + 1; i <= m; ++i) if(vis[i]) res += W[i];
	return res;
}

signed main () {
#ifdef FILE
	freopen(".in", "r", stdin);
	freopen(".out", "w", stdout);
#endif
	read(n), read(k);
	for(int i = 1; i <= k; ++i) read(d_lim[i]);
	for(int i = k + 1; i <= n; ++i) d_lim[i] = INF;
	m = (k - 1) * k >> 1;
	for(int i = 1; i <= n; ++i)
		for(int j = i + 1; j <= n; ++j) {
			if(j > k) { read(W[++m]); X[m] = i, Y[m] = j; } 
			else { read(W[++m1]); X[m1] = i, Y[m1] = j; }
		}
	for(int S = 0; S < (1 << m1); ++S) {
		if(!M1.chk(S) || !M2.chk(S)) continue;
		M1.Init(S); M2.Init(S);
		Ans = min(Ans, Matroid_Intersection(S));
	}
	printf("%d\n", Ans);
	return 0;
}
```

模 拟 退 火 天 下 第 一

------

数据范围很小，因此考虑玄学算法，比如模拟退火。

考虑如何构建算法：我们只考虑前 $k$ 个节点的连边。考虑维护这些点与其它点在最小生成树上的邻接矩阵。

每次在前一个解的基础上生成新解时，我们这样构造：

1. 扫过整个邻接矩阵，以某一概率将当前在生成树上的边删除。
2. 扫过整个邻接矩阵，以同上的概率将一条可以被加入生成树上的边加入生成树。
3. 对前 $k$ 个点中每个点判断其所在的连通块中是否包含前 $k$ 个点以外的其它点。如果为否，则需要将这个连通块连到外面的某点。
4. 遍历所有前 $k$ 个点以外的点间的边，求关于其的最小生成树。

这里面有一些细节：

- 删边加边的概率应与当前温度有关，且在温度低时低，温度高时高。（在代码中，这一概率被我错误地设置成了温度 $T$​ 本身，这意味着在 $T>1$​ 时我的代码实际上是会百分之百删掉边的；但是因为~~数据水~~其它部分写得比较真，所以最后也通过了；这一错误在写题解时才被我发现，大家如果借鉴代码记得修改这部分）

- 在步骤 2 中扫邻接矩阵时应该以随机顺序扫过，这样才能避免加入的边全部集中在前数条中的情形。
- 步骤 3 可以用这样的流程实现：判断连通块中是否存在度数未满的节点，如果存在就从它出发连一条边，这条边应该是所有可能的边中权值最小的，且终点必须是一个包含前 $k$ 个点以外的其它点的连通块。如果不存在这样的节点，就把这个连通块中边全部删除，然后对其中每个节点（显然其度数现在为零，不可能满）分开决策。
- 步骤 4 中的最小生成树可以预先排好序。

如果不出意外的话，这时就可以以 $n^2\alpha$ 的复杂度生成一个必然正确的解。

那么就模拟退火即可。

代码：

```cpp
#include<bits/stdc++.h>
using namespace std;
#define pii pair<int,int>
#define fi first
#define se second
int n,m,k,d[10],g[60][60],dsu[60],p[10],id[10][60];
bool on[10][60],mon[10][60],non[10][60];
int now,mn,nw;
int O,Q;
pii o[1500],q[500];
int find(int x){return dsu[x]==x?x:dsu[x]=find(dsu[x]);}
bool merge(int x,int y){x=find(x),y=find(y);if(x==y)return false;dsu[x]=y;return true;}
const double eps=1e-3;
const double Delta=0.99975;
const double Tin=5000;
double possR(){return 1.0*(rand()*RAND_MAX+rand())/(RAND_MAX*RAND_MAX);}
bool cont[100];
void findoutercontact(int i){
	for(int j=1;j<=n;j++){
		int y=id[i][j];
		if(y<=k&&(p[y]==d[y]||!cont[find(y)]))continue;
		merge(i,y),p[i]++,nw+=g[min(i,y)][max(i,y)],non[min(i,y)][max(i,y)]=true;
		if(y<=k)p[y]++;
		break;
	}
}
void gene(double T){
	nw=0;
	for(int i=1;i<=n;i++)dsu[i]=i,cont[i]=false;
	for(int i=1;i<=k;i++)p[i]=0;
	for(int i=1;i<=k;i++)for(int j=i+1;j<=n;j++)non[i][j]=on[i][j];
	for(int i=1;i<=Q;i++){
		int x=q[i].fi,y=q[i].se;
		if(!non[x][y])continue;
		if(possR()<T){non[x][y]=false;continue;}
		p[x]++;if(y<=k)p[y]++;
		merge(x,y),nw+=g[x][y];
	}
	random_shuffle(q+1,q+Q+1);
	for(int i=1;i<=Q;i++){
		int x=q[i].fi,y=q[i].se;
		if(non[x][y])continue;
		if(p[x]==d[x])continue;
		if(y<=k&&p[y]==d[y])continue;
		if(find(x)==find(y))continue;
		if(possR()>T)continue;
		non[x][y]=true,merge(x,y),nw+=g[x][y],p[x]++;
		if(y<=k)p[y]++;
	}
	for(int i=1;i<=k;i++)for(int j=k+1;j<=n;j++)if(non[i][j])cont[find(i)]=true;
	for(int i=1;i<=k;i++){
		if(cont[find(i)])continue;
		int pos=find(i);
		int spc=-1;
		for(int j=1;j<=k;j++)if(find(j)==pos&&p[j]!=d[j])spc=j;
		if(spc==-1){
			for(int j=1;j<=k;j++){
				if(find(j)!=pos)continue;
				dsu[j]=j,p[j]=0;for(int y=j+1;y<=k;y++)non[j][y]=false;
				cont[j]=false;
				findoutercontact(j);
			}
		}else findoutercontact(spc);
	}
	for(int i=1;i<=O;i++)if(merge(o[i].fi,o[i].se))nw+=g[o[i].fi][o[i].se];
}
void Simulated_Anneal(){
	double T=Tin;
	for(int i=1;i<=k;i++)for(int j=i+1;j<=n;j++)on[i][j]=mon[i][j];
	now=mn;
	while(T>eps){
//		printf("%lf:%d\n",T,now);
		gene(T);
		if(nw<mn){
			mn=now=nw;
			for(int i=1;i<=k;i++)for(int j=i+1;j<=n;j++)on[i][j]=mon[i][j]=non[i][j];
			T*=Delta;
			continue;
		}
		int delta=now-nw;
//		printf("%d,%lf:%lf\n",delta,T,exp(-delta/T));
		if(exp(delta/T)>possR()){
//			printf("%lf:ACCEPTED\n",T);
			now=nw;
			for(int i=1;i<=k;i++)for(int j=i+1;j<=n;j++)on[i][j]=non[i][j];
		}//else printf("%lf:REJECTED\n",T);
		T*=Delta;
	}
} 
void Climbing(){
	for(int t=1;t<=500;t++){
		for(int i=1;i<=k;i++)for(int j=i+1;j<=n;j++)on[i][j]=mon[i][j];
		gene(0.5);
		if(nw<mn){
			mn=nw;
			for(int i=1;i<=k;i++)for(int j=i+1;j<=n;j++)mon[i][j]=non[i][j];
		}
	}
}
int USEDINSORT;
int main(){
	srand(19260817);
	scanf("%d%d",&n,&k);
	for(int i=1;i<=k;i++)scanf("%d",&d[i]);
	for(int i=1;i<=n;i++)for(int j=i+1;j<=n;j++)scanf("%d",&g[i][j]);
	for(int i=1;i<=k;i++){
		for(int j=1;j<=n;j++)id[i][j]=j;
		for(int j=1;j<i;j++)g[i][j]=g[j][i];
		g[i][i]=0x3f3f3f3f;
		USEDINSORT=i;
		sort(id[i]+1,id[i]+n+1,[](int x,int y){return g[USEDINSORT][x]<g[USEDINSORT][y];});
	}
	for(int i=1;i<=k;i++)for(int j=i+1;j<=n;j++)q[++Q]=make_pair(i,j);
	for(int i=k+1;i<=n;i++)for(int j=i+1;j<=n;j++)o[++O]=make_pair(i,j);
	sort(o+1,o+O+1,[](pii&u,pii&v){return g[u.fi][u.se]<g[v.fi][v.se];});
	gene(Tin);
	mn=nw;for(int i=1;i<=k;i++)for(int j=i+1;j<=n;j++)mon[i][j]=non[i][j];
	for(int i=1;i<=5;i++)Climbing(),Simulated_Anneal();
//	for(int i=1;i<=k;i++){for(int j=1;j<=n;j++)printf("%d ",mon[i][j]);puts("");}
	printf("%d\n",mn);
	return 0;
}
```
