using namespace std;
int t;
int n,s;
int main(){
	#ifndef ONLINE_JUDGE
	freopen("CF1566A_1.in","r",stdin);
	freopen("CF1566A_1.out","w",stdout);
	#endif
	scanf("%d",&t);
	while(t--){
		scanf("%d%d",&n,&s);
		printf("%d\n",s/(n-n/2+(n%2==0)));
	}
	return 0;
}
```

