def solve(p1: int, p2: int, direction: bool):
    if p2 - p1 == 2:
        operations.append(p1)
        return
    if direction:
        if p2 - p1 == 1:
            operations.append(p1)
        else:
            operations.append(p2 - 2)
            operations.append(p2 - 1)
            solve(p1, p2 - 2, True)
    else:
