		for(RI i=1;i<=n;i++){
			x=read();
			if(x>maxx) maxy=maxx,maxx=x;
			else if(x>maxy) maxy=x;
		}
		printf("%d\n",min((int)ceil(h*1.0/(maxx+maxy))*2,1+(int)ceil((h-maxx)*1.0/(maxx+maxy))*2));
	}
	return 0;
}
```

