	_FILE();
	read(T);
	while(T--){
		n=read(_);
		memset(a,0,sizeof a);
		buc.clear();
		int CNT=0,ans=0;
		FOR(i,1,n){ read(a[i]); CNT=::max(CNT,++buc[a[i]]); }
		if(CNT>=n/2){ puts("-1"); continue; }
		::sort(a+1,a+n+1);
		FOR(i,1,n/2+1){
			int res=0,rul=0;
			memset(c,0,sizeof c);
			FOR(j,i+1,n){
				int x=a[j]-a[i];
				if(x==0){ ++rul; continue; }
				res=::max(res,x);
				int lim=sqrt(x);
				for(int k=1;k<=lim;k++){
					if(x%k==0){
						++c[k];
						if(x!=k*k){
							++c[x/k];
						}
					}
				}
			}
			int delta=res;
			FOR(i,1,n/2-1-rul){
				while(c[delta]<i){ --delta; }
			}
			ans=::max(ans,delta);
		}
		outn(ans);
	}
	return 0;
