        for(int i=0;i<26;i++)mp[s1[i]]=i;//建立自定义键盘的映射关系
        int ans=0;
        for(int i=1;i<s2.size();i++)ans+=abs(mp[s2[i]]-mp[s2[i-1]]);//mp[c]即是c字符在新键盘上的位置，直接相减即可。
