#include<bits/stdc++.h>
using namespace std;
inline int read(){
    int X=0,w=0; char ch=0;
    while(!isdigit(ch)) {w|=ch=='-';ch=getchar();}
    while(isdigit(ch)) X=(X<<3)+(X<<1)+(ch^48),ch=getchar();
    return w?-X:X;
}
inline void write(int x){
	if(x<0) putchar('-'),x=-x;
    if(x>9) write(x/10);
    putchar(x%10+'0');
}
int n,t; 
int main(){
	t=read();
	while(t--){
		n=read();
		for(int i=1;i<=n;i++) write(i+1),cout<<" ";//输出i+1
		cout<<endl; 
	}
	return 0;
}
```

