	for(int i=1;i<=n;i++)
		scanf("%d",&a[i]);
	memset(f,0x3f,sizeof(f));
	f[0][0]=0;
	for(int i=0;i<=n;i++)
		for(int j=0;j<=n;j++)
			for(int x=i+1;x<=n;x++)
				f[x][j+1]=min(f[x][j+1],f[i][j]+(pos[x+1]-pos[i+1])*a[i+1]); 
	ans=INT_MAX; 
	for(int i=n-k;i<=n;i++)
		ans=min(ans,f[n][i]);
	printf("%d\n",ans);
	return 0;
}
