inline void out(int *a,int l,int r){fo(i,l,r) cout<<*(a+i)<<' ';puts("");}

const int N=1e6+5;
int a[N],b[N],n,m,k;

void solve(){
	cin>>n>>m;
	ll ans=0;
	fo(i,1,n) a[i]=read(),ans+=abs(a[i]);
	fo(i,1,m) b[i]=read();
	k=b[1];fo(i,2,m) k=__gcd(k,b[i]);
	ll v1=0,v2=0;
	fo(i,1,k){
		int mn=1e9,flg=0;
		for(int j=i;j<=n;j+=k){
			if(a[j]<0) flg^=1,sml(mn,-a[j]);
			else sml(mn,a[j]);
		}
		v1+=flg*mn;
		v2+=(flg^1)*mn;
	}
	cout<<ans-2*min(v1,v2)<<'\n';
}

