		sort(b + 1 , b + 1 + m);
		for(int i = 2 ; i <= m ; i ++) b[1] = __gcd(b[1] , b[i]);
		if(b[1] == 1) 
		{
			for(int i = 1 ; i <= n ; i ++) as += Iabs(a[i]);
			printf("%lld\n",as);
